package com.bookreads.user

import org.springframework.dao.DataIntegrityViolationException
import org.springframework.stereotype.Service
import org.springframework.transaction.annotation.Transactional

@Service
class UserService(private val repository: UserRepository) {

    @Transactional
    fun registerOrFetch(username: String): User {
        repository.findByUsername(username)?.let { return it }
        return try {
            repository.saveAndFlush(User(username = username))
        } catch (_: DataIntegrityViolationException) {
            repository.findByUsername(username)
                ?: error("User registration failed for username=$username")
        }
    }

    @Transactional(readOnly = true)
    fun findByUsername(username: String): User? = repository.findByUsername(username)
}
