package com.bookreads.session

import org.springframework.data.jpa.repository.JpaRepository
import org.springframework.data.jpa.repository.Modifying
import org.springframework.data.jpa.repository.Query
import org.springframework.data.repository.query.Param
import java.time.OffsetDateTime

interface SessionRepository : JpaRepository<ReadingSession, Long> {

    fun findByClientId(clientId: String): ReadingSession?

    @Modifying(clearAutomatically = true, flushAutomatically = true)
    @Query(
        value = """
            INSERT INTO reading_sessions (client_id, user_id, book_title, started_at, ended_at, duration_sec)
            VALUES (:clientId, :userId, :bookTitle, :startedAt, :endedAt, :durationSec)
            ON CONFLICT (client_id) DO UPDATE SET
                book_title   = EXCLUDED.book_title,
                ended_at     = EXCLUDED.ended_at,
                duration_sec = EXCLUDED.duration_sec
        """,
        nativeQuery = true,
    )
    fun upsert(
        @Param("clientId") clientId: String,
        @Param("userId") userId: Long,
        @Param("bookTitle") bookTitle: String,
        @Param("startedAt") startedAt: OffsetDateTime,
        @Param("endedAt") endedAt: OffsetDateTime?,
        @Param("durationSec") durationSec: Long,
    )
}
