package com.bookreads.session

import com.bookreads.dto.SessionDto
import com.bookreads.dto.SessionSyncRequest
import com.bookreads.user.UserService
import org.springframework.http.HttpStatus
import org.springframework.stereotype.Service
import org.springframework.transaction.annotation.Transactional
import org.springframework.web.server.ResponseStatusException
import java.time.OffsetDateTime

@Service
class SessionService(
    private val repository: SessionRepository,
    private val userService: UserService,
) {

    @Transactional
    fun sync(request: SessionSyncRequest): SessionDto {
        val user = userService.findByUsername(request.username)
            ?: throw ResponseStatusException(HttpStatus.BAD_REQUEST, "User not found: ${request.username}")
        repository.upsert(
            clientId = request.clientId,
            userId = user.id,
            bookTitle = request.bookTitle,
            startedAt = OffsetDateTime.parse(request.startedAt),
            endedAt = request.endedAt?.let { OffsetDateTime.parse(it) },
            durationSec = request.durationSec,
        )
        return repository.findByClientId(request.clientId)!!.toDto()
    }

    @Transactional(readOnly = true)
    fun findByClientId(clientId: String): SessionDto? =
        repository.findByClientId(clientId)?.toDto()
}

fun ReadingSession.toDto() = SessionDto(
    clientId = clientId,
    username = user.username,
    bookTitle = bookTitle,
    startedAt = startedAt.toString(),
    endedAt = endedAt?.toString(),
    durationSec = durationSec,
)
