package com.bookreads.session

import com.bookreads.dto.SessionDto
import org.springframework.http.ResponseEntity
import org.springframework.web.bind.annotation.ExceptionHandler
import org.springframework.web.bind.annotation.GetMapping
import org.springframework.web.bind.annotation.PathVariable
import org.springframework.web.bind.annotation.PostMapping
import org.springframework.web.bind.annotation.RequestBody
import org.springframework.web.bind.annotation.RequestMapping
import org.springframework.web.bind.annotation.RestController

@RestController
@RequestMapping("/api/v1/sessions")
class SessionController(
    private val sessionService: SessionService,
) {
    data class StartSessionRequest(
        val username: String,
        val bookTitle: String,
    )

    data class EndSessionRequest(
        val username: String,
    )

    @PostMapping("/start")
    fun startSession(
        @RequestBody request: StartSessionRequest,
    ): ResponseEntity<SessionDto> = ResponseEntity.ok(sessionService.startSession(request.username, request.bookTitle))

    @PostMapping("/end")
    fun endSession(
        @RequestBody request: EndSessionRequest,
    ): ResponseEntity<SessionDto> = ResponseEntity.ok(sessionService.endSession(request.username))

    @GetMapping("/active/{username}")
    fun getActiveSession(
        @PathVariable username: String,
    ): ResponseEntity<SessionDto> {
        val session = sessionService.getActiveSession(username)
        return if (session != null) ResponseEntity.ok(session) else ResponseEntity.noContent().build()
    }

    @ExceptionHandler(NoSuchElementException::class)
    fun handleNotFound(ex: NoSuchElementException): ResponseEntity<Map<String, String>> =
        ResponseEntity.status(404).body(mapOf("error" to (ex.message ?: "Not found")))

    @ExceptionHandler(IllegalStateException::class)
    fun handleConflict(ex: IllegalStateException): ResponseEntity<Map<String, String>> =
        ResponseEntity.status(409).body(mapOf("error" to (ex.message ?: "Conflict")))
}

package com.bookreads.session

import org.springframework.data.jpa.repository.JpaRepository
import java.util.Optional

interface SessionRepository : JpaRepository<ReadingSession, Long> {
    fun findByUserIdAndEndedAtIsNull(userId: Long): Optional<ReadingSession>
}

package com.bookreads.session

import com.bookreads.dto.SessionDto
import com.bookreads.user.UserRepository
import org.springframework.stereotype.Service
import org.springframework.transaction.annotation.Transactional
import java.time.Instant

@Service
class SessionService(
    private val sessionRepository: SessionRepository,
    private val userRepository: UserRepository,
    private val entityManager: jakarta.persistence.EntityManager,
) {
    @Transactional
    fun startSession(
        username: String,
        bookTitle: String,
    ): SessionDto {
        val user =
            userRepository
                .findByUsername(username)
                .orElseThrow { NoSuchElementException("User not found: $username") }
        sessionRepository.findByUserIdAndEndedAtIsNull(user.id).ifPresent {
            throw IllegalStateException("User $username already has an active session")
        }
        val session = sessionRepository.save(ReadingSession(user = user, bookTitle = bookTitle))
        sessionRepository.flush()
        entityManager.refresh(session)
        return session.toDto()
    }

    @Transactional
    fun endSession(username: String): SessionDto {
        val user =
            userRepository
                .findByUsername(username)
                .orElseThrow { NoSuchElementException("User not found: $username") }
        val session =
            sessionRepository
                .findByUserIdAndEndedAtIsNull(user.id)
                .orElseThrow { NoSuchElementException("No active session for user: $username") }
        session.endedAt = Instant.now()
        sessionRepository.save(session)
        sessionRepository.flush()
        entityManager.refresh(session)
        return session.toDto()
    }

    @Transactional(readOnly = true)
    fun getActiveSession(username: String): SessionDto? {
        val user =
            userRepository
                .findByUsername(username)
                .orElseThrow { NoSuchElementException("User not found: $username") }
        return sessionRepository
            .findByUserIdAndEndedAtIsNull(user.id)
            .map { it.toDto() }
            .orElse(null)
    }

    private fun ReadingSession.toDto() =
        SessionDto(
            id = id,
            username = user.username,
            bookTitle = bookTitle,
            startedAt = startedAt.toString(),
            endedAt = endedAt?.toString(),
            durationSec = durationSec,
        )
}

