                startedAt = Instant.now().toString(),
                endedAt = Instant.now().toString(),
                durationSec = 300,
            )
        restTemplate.postForEntity("/api/v1/sessions/sync", syncRequest, Any::class.java)

        val response =
            restTemplate.getForEntity(
                "/api/v1/leaderboard?window=alltime",
                Array<LeaderboardEntryDto>::class.java,
            )
        assertThat(response.statusCode).isEqualTo(HttpStatus.OK)
        val entry = response.body?.find { it.username == username }
        assertThat(entry).isNotNull
        assertThat(entry?.totalSec).isEqualTo(300)
    }
}

