package com.bookreads

import com.bookreads.dto.UserDto
import com.bookreads.user.UserController
import org.assertj.core.api.Assertions.assertThat
import org.junit.jupiter.api.Test

class UserIntegrationTest : AbstractIntegrationTest() {

    @Test
    fun `register new user returns UserDto with correct username`() {
        val response = restTemplate.postForEntity(
            "/api/v1/users",
            UserController.RegisterRequest("alice-test"),
            UserDto::class.java,
        )
        assertThat(response.statusCode.is2xxSuccessful).isTrue()
        assertThat(response.body?.username).isEqualTo("alice-test")
        assertThat(response.body?.id).isPositive()
    }

    @Test
    fun `register same username twice returns same user id`() {
        val request = UserController.RegisterRequest("bob-test")
        val first = restTemplate.postForEntity("/api/v1/users", request, UserDto::class.java)
        val second = restTemplate.postForEntity("/api/v1/users", request, UserDto::class.java)
        assertThat(first.body?.id).isEqualTo(second.body?.id)
    }

    @Test
    fun `get user by username returns 200 with correct data`() {
        restTemplate.postForEntity("/api/v1/users", UserController.RegisterRequest("charlie-test"), UserDto::class.java)
        val response = restTemplate.getForEntity("/api/v1/users/charlie-test", UserDto::class.java)
        assertThat(response.statusCode.is2xxSuccessful).isTrue()
        assertThat(response.body?.username).isEqualTo("charlie-test")
    }

    @Test
    fun `get unknown user returns 404`() {
        val response = restTemplate.getForEntity("/api/v1/users/unknown-user-xyz", String::class.java)
        assertThat(response.statusCode.value()).isEqualTo(404)
    }
}

