import com.bookreads.user.User
import jakarta.persistence.Column
import jakarta.persistence.Entity
import jakarta.persistence.FetchType
import jakarta.persistence.GeneratedValue
import jakarta.persistence.GenerationType
import jakarta.persistence.Id
import jakarta.persistence.JoinColumn
import jakarta.persistence.ManyToOne
import jakarta.persistence.Table
import java.time.OffsetDateTime

@Entity
@Table(name = "reading_sessions")
class ReadingSession(
    @Id
    @GeneratedValue(strategy = GenerationType.IDENTITY)
    val id: Long = 0,

    @Column(name = "client_id", nullable = false, unique = true)
    val clientId: String,

    @ManyToOne(fetch = FetchType.EAGER)
    @JoinColumn(name = "user_id", nullable = false)
    val user: User,

    @Column(name = "book_title", nullable = false)
    var bookTitle: String,

    @Column(name = "started_at", nullable = false, updatable = false)
    val startedAt: OffsetDateTime,

    @Column(name = "ended_at")
    var endedAt: OffsetDateTime? = null,

    @Column(name = "duration_sec", nullable = false)
    var durationSec: Long,
) {
    override fun equals(other: Any?): Boolean {
        if (this === other) return true
        if (other !is ReadingSession) return false
        return clientId == other.clientId
    }

    override fun hashCode(): Int = clientId.hashCode()
}

package com.bookreads.leaderboard

import com.bookreads.dto.LeaderboardEntryDto
import org.springframework.jdbc.core.JdbcTemplate
import org.springframework.stereotype.Service
import java.time.OffsetDateTime
import java.time.ZoneOffset

@Service
class LeaderboardService(private val jdbc: JdbcTemplate) {

    fun getLeaderboard(window: String): List<LeaderboardEntryDto> {
        val windowStart: OffsetDateTime? = when (window.lowercase()) {
            "daily" -> OffsetDateTime.now(ZoneOffset.UTC).minusDays(1)
            "weekly" -> OffsetDateTime.now(ZoneOffset.UTC).minusWeeks(1)
            "monthly" -> OffsetDateTime.now(ZoneOffset.UTC).minusMonths(1)
            else -> null
        }

        return if (windowStart != null) {
            jdbc.query(
                """
                SELECT u.username, SUM(s.duration_sec) AS total_sec, COUNT(s.id) AS session_count
                FROM reading_sessions s
                JOIN users u ON s.user_id = u.id
                WHERE s.started_at >= ?
                GROUP BY u.username
                ORDER BY total_sec DESC
                """,
                { rs, rowNum ->
                    LeaderboardEntryDto(
                        rank = rowNum + 1,
                        username = rs.getString("username"),
                        totalSec = rs.getLong("total_sec"),
                        sessionCount = rs.getInt("session_count"),
                    )
                },
                windowStart,
            )
        } else {
            jdbc.query(
                """
                SELECT u.username, SUM(s.duration_sec) AS total_sec, COUNT(s.id) AS session_count
                FROM reading_sessions s
                JOIN users u ON s.user_id = u.id
                GROUP BY u.username
                ORDER BY total_sec DESC
                """,
            ) { rs, rowNum ->
                LeaderboardEntryDto(
                    rank = rowNum + 1,
                    username = rs.getString("username"),
                    totalSec = rs.getLong("total_sec"),
                    sessionCount = rs.getInt("session_count"),
                )
            }
        }
    }
}

package com.bookreads.user

import org.springframework.dao.DataIntegrityViolationException
import org.springframework.stereotype.Service
import org.springframework.transaction.annotation.Transactional

@Service
class UserService(private val repository: UserRepository) {

    @Transactional
    fun registerOrFetch(username: String): User {
        repository.findByUsername(username)?.let { return it }
        return try {
            repository.saveAndFlush(User(username = username))
        } catch (_: DataIntegrityViolationException) {
            repository.findByUsername(username)
                ?: error("User registration failed for username=$username")
        }
    }

    @Transactional(readOnly = true)
    fun findByUsername(username: String): User? = repository.findByUsername(username)
}

